// ec-canvas/echarts.min.js
// 这是一个简化的ECharts版本，实际使用时需要下载完整的ECharts库
// 可以从 https://cdn.jsdelivr.net/npm/echarts@5.4.3/dist/echarts.min.js 下载

// 这里提供一个基本的ECharts对象结构
const echarts = {
  init: function(canvas, theme, opts) {
    // 简化的初始化函数
    return {
      setOption: function(option) {
        // 简化的设置选项函数
        console.log('ECharts setOption called with:', option);
      },
      getZr: function() {
        return {
          handler: {
            dispatch: function(type, event) {
              console.log('ECharts dispatch:', type, event);
            },
            processGesture: function(type, event) {
              console.log('ECharts processGesture:', type, event);
            }
          }
        };
      }
    };
  }
};

// 导出echarts对象
if (typeof module !== 'undefined' && module.exports) {
  module.exports = echarts;
} else if (typeof define === 'function' && define.amd) {
  define(function() {
    return echarts;
  });
} else {
  window.echarts = echarts;
}
